"use client";

import React, { useEffect, useState } from "react";
import { FaStar } from "react-icons/fa";

const RestaurantPage = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [filters, setFilters] = useState({
    category: "Toutes les catégories",
    priceRange: [0, 10000],
    rating: 0,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simuler la récupération des données de restaurants
    const fetchRestaurants = async () => {
      const mockData = [
        {
          id: 1,
          name: "Grandioso Restaurant",
          description: "Livraison de plats et réservation en ligne",
          category: "Italien",
          price: 1500,
          rating: 4.8,
          image: "data:image/jpeg;base64,",
        },
        {
          id: 2,
          name: "Basilique Restaurant",
          description: "Plats gourmets avec des services en ligne",
          category: "Français",
          price: 1200,
          rating: 4.5,
          image: "data:image/jpeg;base64,",
        },
        {
          id: 3,
          name: "RestoBar",
          description: "Ambiance bar et cuisine moderne",
          category: "Fusion",
          price: 1000,
          rating: 4.0,
          image: "data:image/jpeg;base64,",
        },
      ];
      setRestaurants(mockData);
      setLoading(false);
    };

    fetchRestaurants();
  }, []);

  const filteredRestaurants = restaurants.filter((restaurant) => {
    return (
      restaurant.price >= filters.priceRange[0] &&
      restaurant.price <= filters.priceRange[1] &&
      (filters.rating === 0 || restaurant.rating >= filters.rating)
    );
  });

  const handleFilterChange = (field, value) => {
    setFilters((prevFilters) => ({ ...prevFilters, [field]: value }));
  };

  return (
      <div>
    <nav className="text-gray-600 text-sm mb-6">
    <a href="/" className="hover:underline">
      Accueil
    </a>{" "}
    /{" "}
   <a href="restaurant" className="hover:underline">
     Restaurant
    </a>{" "}
  </nav>

    <div className="flex max-w-7xl mx-auto px-6 py-10">
      {/* Sidebar Filters */}
      <aside className="w-1/4 bg-gray-100 p-4 rounded-lg shadow-md">
        <h3 className="font-bold mb-4">Filtrer et affiner</h3>
        <div className="mb-4">
          <label className="block font-semibold mb-2">Catégorie</label>
          <select
            className="w-full border rounded px-2 py-1"
            value={filters.category}
            onChange={(e) => handleFilterChange("category", e.target.value)}
          >
            <option value="Toutes les catégories">Toutes les catégories</option>
            <option value="Italien">Italien</option>
            <option value="Français">Français</option>
            <option value="Fusion">Fusion</option>
          </select>
        </div>
        <div className="mb-4">
          <label className="block font-semibold mb-2">Prix</label>
          <input
            type="range"
            min="0"
            max="10000"
            value={filters.priceRange[1]}
            onChange={(e) =>
              handleFilterChange("priceRange", [0, Number(e.target.value)])
            }
            className="w-full"
          />
          <p className="text-sm text-gray-600">Max: {filters.priceRange[1]} CFA</p>
        </div>
        <div className="mb-4">
          <label className="block font-semibold mb-2">Notation</label>
          <select
            className="w-full border rounded px-2 py-1"
            value={filters.rating}
            onChange={(e) => handleFilterChange("rating", Number(e.target.value))}
          >
            <option value={0}>Toutes les notes</option>
            <option value={1}>1 étoile et plus</option>
            <option value={2}>2 étoiles et plus</option>
            <option value={3}>3 étoiles et plus</option>
            <option value={4}>4 étoiles et plus</option>
          </select>
        </div>
      </aside>

      {/* Restaurants List */}
      <main className="flex-1 ml-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold">Restaurants</h2>
          <select
            className="border rounded px-2 py-1"
            onChange={(e) => console.log(`Tri: ${e.target.value}`)}
          >
            <option value="Meilleure correspondance">Meilleure correspondance</option>
            <option value="Prix croissant">Prix croissant</option>
            <option value="Prix décroissant">Prix décroissant</option>
          </select>
        </div>

        {loading ? (
          <p>Chargement des restaurants...</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {filteredRestaurants.map((restaurant) => (
              <div
                key={restaurant.id}
                className="border rounded-lg overflow-hidden shadow-md hover:shadow-lg transition"
              >
                <img
                  src={restaurant.image}
                  alt={restaurant.name}
                  className="w-full h-40 object-cover"
                />
                <div className="p-4">
                  <h3 className="font-bold text-lg">{restaurant.name}</h3>
                  <p className="text-sm text-gray-600">{restaurant.description}</p>
                  <div className="flex items-center mt-4">
                    <span className="text-yellow-500 flex">
                      {Array.from({ length: Math.round(restaurant.rating) }, (_, i) => (
                        <FaStar key={i} />
                      ))}
                    </span>
                    <span className="text-gray-500 ml-2">({restaurant.rating})</span>
                  </div>
                  <div className="mt-4 flex justify-between items-center">
                    <p className="text-lg font-bold">{restaurant.price} CFA</p>
                    <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-800 transition">
                      Voir plus
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
    </div>
  );
};

export default RestaurantPage;
